import React from 'react';
import bridge from '@vkontakte/vk-bridge';
import '@vkontakte/vkui/dist/vkui.css';
import './css/App.css'
import html2canvas from 'html2canvas';

import { Panel, Title, Headline, Footer, View , SimpleCell, ScreenSpinner, Avatar , Button, Snackbar } from '@vkontakte/vkui';

import Icon28StoryOutline from '@vkontakte/icons/dist/28/story_outline';

import cookieIcon from './img/cookie.png';
import paper from './img/paper.jpg';
import downwards_black_arrow from './img/downwards-black-arrow.png';

import Lottie from 'react-lottie'
import animDataCookie from './animations/cookie.json'

const random = require('random');

let app_id = 7554927,
	group_id = 197570628;

class App extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			popout: null,
			activePanel: 'main',
			scheme: 'bright_light',

			can_generate: true,
			first_loaded: false,
			cookieAnimStarted: true
		};

		this.componentDidMount = this.componentDidMount.bind(this);
		this.go = this.go.bind(this);
	}

	async componentDidMount () {
		bridge.subscribe(async ({ detail: { type, data }}) => {
			if(type !== undefined) console.log(type, data);
			if (type === 'VKWebAppUpdateConfig') {
				const schemeAttribute = document.createAttribute('scheme');
				schemeAttribute.value = false ? data.scheme ? data.scheme : 'bright_light' : 'bright_light';
				document.body.attributes.setNamedItem(schemeAttribute);
				this.setState({ scheme: schemeAttribute.value });
				if(bridge.supports('VKWebAppSetViewSettings')){
					bridge.send('VKWebAppSetViewSettings', {status_bar_style: 'light', action_bar_color: 'none'});
				}
			}else if (type === 'VKWebAppViewRestore') {
				this.setState({ popout: null, screen: false });
				let shapes = document.getElementsByClassName('bg_shape_container');
				for(let element of shapes){
					try{
						console.log('delete', element);
						element.remove();
					}catch (e) {
						console.error(e);
					}
				}
			}
		});

		let user = await bridge.sendPromise('VKWebAppGetUserInfo');
		this.setState({ photo: user.photo_max_orig, name: user.first_name + ' ' + user.last_name });

		this.initializeTimer();

		await this.updateStorage();

		await bridge.send('VKWebAppInit');
	}

	async updateStorage () {
		let sValues = await bridge.sendPromise('VKWebAppStorageGet', {keys: ['used1_____', 'used2_____']});
		sValues = sValues.keys;
		let data = {};
		try{
			for(let value of sValues){
				let nmbr = Number(value.value);
				data[value.key] = nmbr > 0 ? nmbr : value.value;
			}
		}catch (e) {
			console.error('e3', e)
		}
		this.setState({ data, sValues });
		return;
	}

	go(panel) {
		this.setState({activePanel: panel});
	}

	sleep(ms) {
		return new Promise(resolve => setTimeout(resolve, ms));
	}

	initializeTimer() {
		let imgs = [cookieIcon];
		setInterval(()=>{
			try{
				if(this.state.screen) return;
				let shape_container = document.createElement('div');
				shape_container.className = 'bg_shape_container';
				let shape = document.createElement('img');
				shape.crossOrigin = 'anonymous';
				shape.className = 'bg_shape light_outline';
				shape.src = imgs[this.random(0, imgs.length)];
				shape.style.left = (this.random(-15, 90)) + 'vw';
				shape_container.appendChild(shape);
				document.getElementById('bg_shapes').appendChild(shape_container);
				for(let i = 0; i < 5; i++){
					shape.style.setProperty('--offset-x-' + i, this.random(-20, 20) +'vw');
				}
				setTimeout(()=>{
					try{
						shape_container.remove();
					}catch (e) {}
				}, 10000);
			}catch (e) {}
		}, 600);
	}

	random(min, max) {
		return Math.floor(Math.random() * (max - min) + min);
	}

	viewportToPixels(value) {
		let parts = value.match(/([0-9\.]+)(vh|vw)/);
		let q = Number(parts[1])
		let side = window[['innerHeight', 'innerWidth'][['vh', 'vw'].indexOf(parts[2])]];
		return side * (q/100)
	}

	convertMiliseconds(miliseconds) {
		let days, hours, minutes, seconds, total_hours, total_minutes, total_seconds;

		total_seconds = parseInt(Math.floor(miliseconds / 1000));
		total_minutes = parseInt(Math.floor(total_seconds / 60));
		total_hours = parseInt(Math.floor(total_minutes / 60));
		days = parseInt(Math.floor(total_hours / 24));

		seconds = parseInt(total_seconds % 60);
		minutes = parseInt(total_minutes % 60);
		hours = parseInt(total_hours % 24);

		return hours+'ч. '+minutes+'мин.';
	}

	render() {
		return (
			<View activePanel={this.state.activePanel} popout={this.state.popout}>
				<Panel id='main' style={{ zIndex: 1 }}>
					<div className={'animated-background'} style={{ width: '100vw', height: '100vh' }}>
						<div id='bg_shapes'/>
						<div className='absolute_centered' style={{ transform: 'translate(-50%, -80%)' }}>
							<div className={'centered_'} style={{ position: 'absolute', top: '10vh', width: '100%', color: 'white', textAlign: 'center' }}>
								<img src={cookieIcon} style={{ marginRight: 8, display: this.state.screen && 'none' }} height={32}/>
								<Title level='1' weight='bold'>
									{!this.state.screen ?
										'Печенька'
										:
										'Моё предсказание из печеньки на сегодня'.toUpperCase()
									}
								</Title>
							</div>
							<Lottie style={{pointerEvents: 'none', zIndex: -1, opacity: this.state.showText && .5 }} options={{
								animationData: animDataCookie,
								rendererSettings: {
									preserveAspectRatio: 'xMidYMid slice'
								}
							}} width={this.viewportToPixels('70vw')} isStopped={this.state.cookieAnimStarted}
							/>
							<div className='absolute_centered' style={{ backgroundImage: `url(${paper})`, backgroundRepeat: 'repeat', display: !this.state.showText && 'none', borderRadius: this.viewportToPixels('1vh') }}>
								<Headline weight='bold' style={{ padding: '16px', width: '50vw', textAlign: 'center' }}>Очень длинный текст с предсказанием</Headline>
							</div>
							{
								this.state.screen &&
								<Button id={'btn_story'} disabled={!this.state.cookieAnimStarted} size={'xl'} style={{ backgroundColor: 'white', color: 'black' }} onClick={async ()=>{
									if(this.state.data.used2_____ !== false){
										let last = Date.now() - this.state.data.used2_____;
										if(last < 24 * 60 * 60 * 1000) {
											this.setState({ snackbar:
													<Snackbar
														layout='vertical'
														onClose={() => this.setState({ snackbar: null })}
													>
														Для повтора подождите { this.convertMiliseconds(24 * 60 * 60 * 1000 - last) }
													</Snackbar>
											});
											return;
										}
									}

									if(this.state.used){
										if(!this.state.shared){
											this.setState({ snackbar:
													<Snackbar
														layout='vertical'
														onClose={() => this.setState({ snackbar: null })}
													>
														Необходимо сначала поделиться в истории
													</Snackbar>
											});
											return;
										}
									}

									if(!this.state.data.used1_____){
										await bridge.sendPromise('VKWebAppStorageSet', {key: 'used1_____', value: Date.now().toString()});
									}else{
										await bridge.sendPromise('VKWebAppStorageSet', {key: 'used2_____', value: Date.now().toString()});
									}
									await this.updateStorage();

									this.setState({ cookieAnimStarted: false, showText: false });
									setTimeout(()=>{
										this.setState({ cookieAnimStarted: true, showText: true, used: true });
									}, 2250)
								}}>{ this.state.screen ? 'Открыть свою печеньку' : this.state.showText ? 'Попробовать снова' : 'Разломить печеньку' }</Button>
							}
						</div>
						{
							false && this.state.screen &&
								<div style={{ position: 'absolute', bottom: '20vh' }} >
									<Title level={2} weight='semibold' style={{ color: 'white' }}>
										<span className={'centered'} style={{ textAlign: 'center' }}>Переходи в приложение, чтобы получить предсказание</span>
										<br/>
										<div className={'centered'}>
											<img crossOrigin={'anonymous'} height='26px' src={downwards_black_arrow}/>
											<img crossOrigin={'anonymous'} style={{ marginLeft: '8px' }} height='26px' src={downwards_black_arrow}/>
											<img crossOrigin={'anonymous'} style={{ marginLeft: '8px' }} height='26px' src={downwards_black_arrow}/>
										</div>
									</Title>
								</div>
						}
						{
							(true || this.state.showText) && !this.state.screen &&
							<div style={{ position: 'absolute' , bottom: '4vh', width: '100vw', zIndex: 1 }}>
								<Button onClick={async ()=>{
									try{
										const button = document.getElementById('btn_story');
										this.setState({ popout: <ScreenSpinner/>, screen: true });
										await this.sleep(750);
										let element = document.getElementById('main');
										html2canvas(element, { allowTaint: true }).then(async canvas => {
											let blob = canvas.toDataURL('image/jpg');
											let bodyRect = document.body.getBoundingClientRect(),
												elemRect = button.getBoundingClientRect(),
												offset   = elemRect.top - bodyRect.top;

											try{
												await bridge.send('VKWebAppShowStoryBox', {
													background_type: 'image', blob, attachment: { url: 'https://vk.com/app' + app_id, text: 'open', type: 'url' },
													stickers: [
														{
															sticker_type: 'ren'
														}
													]
												});
												this.setState({ shared: true });
											}catch (e) {}
											this.setState({ popout: null, screen: false });
										});
									}catch (e) {
										console.error(e);
										this.setState({ snackbar:
												<Snackbar
													layout='vertical'
													onClose={() => this.setState({ snackbar: null })}
												>
													Ошибка
												</Snackbar>
										});
										this.setState({ popout: null, screen: false });
									}
								}} style={{ background: '#ffffff00', color: 'white', fontSize: '20px', letterSpacing: '1px' }} before={<Icon28StoryOutline/>} size='xl'>Поделиться в истории</Button>
							</div>
						}

						{this.state.snackbar}
					</div>
				</Panel>
			</View>
		);
	}
}

export default App;
