import React from 'react';
import bridge from '@vkontakte/vk-bridge';
import '@vkontakte/vkui/dist/vkui.css';
import './css/App.css'
import html2canvas from 'html2canvas';

import { Panel, Title, Headline, Footer, View , SimpleCell, ScreenSpinner, Avatar , Button, Snackbar } from '@vkontakte/vkui';

import Icon28StoryOutline from '@vkontakte/icons/dist/28/story_outline';

import cookieIcon from './img/cookie.png';
import paper from './img/paper.jpg';
import downwards_black_arrow from './img/downwards-black-arrow.png';

import Lottie from 'react-lottie'
import animDataCookie from './animations/cookie.json'

const random = require('random');

let app_id = 7554927,
	group_id = 197570628;

class App extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			popout: null,
			activePanel: 'main',
			scheme: 'bright_light',

			can_generate: true,
			first_loaded: false,
			cookieAnimStarted: true
		};

		this.componentDidMount = this.componentDidMount.bind(this);
		this.go = this.go.bind(this);
	}

	async componentDidMount () {
		bridge.subscribe(async ({ detail: { type, data }}) => {
			if(type !== undefined) console.log(type, data);
			if (type === 'VKWebAppUpdateConfig') {
				const schemeAttribute = document.createAttribute('scheme');
				schemeAttribute.value = false ? data.scheme ? data.scheme : 'bright_light' : 'bright_light';
				document.body.attributes.setNamedItem(schemeAttribute);
				this.setState({ scheme: schemeAttribute.value });
			}
		});

		let user = await bridge.sendPromise('VKWebAppGetUserInfo');
		this.setState({ photo: user.photo_max_orig, name: user.first_name + ' ' + user.last_name });

		this.initializeTimer();

		await bridge.send('VKWebAppInit');
	}

	go(panel) {
		this.setState({activePanel: panel});
	}

	sleep(ms) {
		return new Promise(resolve => setTimeout(resolve, ms));
	}

	initializeTimer() {
		let imgs = [cookieIcon];
		setInterval(()=>{
			try{
				let shape_container = document.createElement('div');
				shape_container.className = 'bg_shape_container';
				let shape = document.createElement('img');
				shape.crossOrigin = 'anonymous';
				shape.className = 'bg_shape light_outline';
				shape.src = imgs[this.random(0, imgs.length)];
				shape.style.left = (this.random(-15, 90)) + 'vw';
				shape_container.appendChild(shape);
				document.getElementById('bg_shapes').appendChild(shape_container);
				for(let i = 0; i < 5; i++){
					shape.style.setProperty('--offset-x-' + i, this.random(-20, 20) +'vw');
				}
				setTimeout(()=>{
					try{
						shape_container.remove();
					}catch (e) {}
				}, 10000);
			}catch (e) {}
		}, 600);
	}

	random(min, max) {
		return Math.floor(Math.random() * (max - min) + min);
	}

	viewportToPixels(value) {
		let parts = value.match(/([0-9\.]+)(vh|vw)/)
		let q = Number(parts[1])
		let side = window[['innerHeight', 'innerWidth'][['vh', 'vw'].indexOf(parts[2])]];
		return side * (q/100)
	}

	render() {
		return (
			<View activePanel={this.state.activePanel} popout={this.state.popout}>
				<Panel id='main' style={{ zIndex: 1 }}>
					<div className={'animated-background'} style={{ width: '100vw', height: '100vh' }}>
						<div id='bg_shapes'/>
						<div className='absolute_centered' style={{ transform: 'translate(-50%, -80%)' }}>
							<div className={'centered_'} style={{ position: 'absolute', top: '10vh', width: '100%', color: 'white' }}><img src={cookieIcon} style={{ marginRight: 8 }} height={32}/> <Title level='1' weight='bold'>Печенька</Title></div>
							<Lottie style={{pointerEvents: 'none', zIndex: -1, opacity: this.state.showText && .5, transform: this.state.screen && 'translatey(20%)' }} options={{
								animationData: animDataCookie,
								rendererSettings: {
									preserveAspectRatio: 'xMidYMid slice'
								}
							}} width={this.viewportToPixels('70vw')} isStopped={this.state.cookieAnimStarted}
							/>
							<div className='absolute_centered' style={{ backgroundImage: `url(${paper})`, backgroundRepeat: 'repeat', display: !this.state.showText && 'none', borderRadius: this.viewportToPixels('1vh') }}>
								<Headline weight='bold' style={{ padding: '16px', width: '50vw', textAlign: 'center' }}>Очень длинный текст предсказания</Headline>
							</div>
							{
								!this.state.screen &&
								<Button disabled={!this.state.cookieAnimStarted} size={'xl'} style={{ backgroundColor: 'white', color: 'black' }} onClick={()=>{
									this.setState({ cookieAnimStarted: false, showText: false });
									setTimeout(()=>{
										this.setState({ cookieAnimStarted: true, showText: true });
									}, 2250)
								}}>Разломить печеньку</Button>
							}
						</div>
						{
							this.state.screen &&
								<div style={{ position: 'absolute', bottom: '20vh' }} className={'centered'}>
									<Title level={2} weight='semibold' style={{ color: 'white' }}>
										Переходи в приложение, чтобы получить предсказание
										<br/>
										<img crossOrigin={'anonymous'} style={{ marginTop: '12px' }} height='26px' src={downwards_black_arrow}/>
										<img crossOrigin={'anonymous'} style={{ marginLeft: '8px' }} height='26px' src={downwards_black_arrow}/>
										<img crossOrigin={'anonymous'} style={{ marginLeft: '8px' }} height='26px' src={downwards_black_arrow}/>
									</Title>
								</div>
						}
						{
							this.state.showText && !this.state.screen &&
							<div style={{ position: 'absolute' , bottom: '1vh', width: '100vw', zIndex: 1 }}>
								<Button onClick={async ()=>{
									try{
										this.setState({ popout: <ScreenSpinner/>, screen: true });
										await this.sleep(750);
										let element = document.getElementById('main');
										html2canvas(element, { allowTaint: true }).then(async canvas => {
											let blob = canvas.toDataURL('image/jpg');
											try{
												await bridge.send('VKWebAppShowStoryBox', { background_type: 'image', blob, attachment: { url: 'https://vk.com/app' + app_id, text: 'open', type: 'url' } });
												this.setState({ shared: true });
											}catch (e) {}
											this.setState({ popout: null, screen: false });
										});
									}catch (e) {
										console.error(e);
										this.setState({ snackbar:
												<Snackbar
													layout='vertical'
													onClose={() => this.setState({ snackbar: null })}
												>
													Ошибка
												</Snackbar>
										});
										this.setState({ popout: null, screen: false });
									}
								}} style={{ background: '#ffffff00', color: 'white', fontSize: '20px', letterSpacing: '1px' }} before={<Icon28StoryOutline/>} size='xl'>Поделиться в истории</Button>
							</div>
						}

						{this.state.snackbar}
					</div>
				</Panel>
			</View>
		);
	}
}

export default App;
